create table cp_device
(
    id                  bigint auto_increment comment '主键'
        primary key,
    user_id             varchar(255) null comment '用户ID',
    device_name         varchar(200) null comment '设备名称',
    device_sn           varchar(200) not null comment '设备序列号',
    device_type         varchar(200) null comment '设备类型',
    sim_card            varchar(200) null comment 'sim卡',
    positioning_mode    varchar(200) null comment '定位模式',
    device_manufacturer varchar(200) null comment '设备厂商',
    device_company      varchar(200) null comment '所属企业ID',
    device_project      varchar(200) null comment '所属项目',
    create_time         varchar(200) null comment '设备接入时间',
    device_group        varchar(200) null comment '设备分组',
    link_type           varchar(255) null comment '设备协议',
    is_on               varchar(255) null comment '状态',
    create_date         datetime     null comment '创建时间',
    founder             varchar(255) null comment '创建人',
    modify_time         varchar(255) null comment '修改时间',
    modifier            varchar(255) null comment '修改人',
    lat                 varchar(255) null comment '纬度',
    lng                 varchar(255) null comment '经度',
    device_id           int(20)      null comment '设备ID',
    device_no           varchar(32)  null,
    is_line             int(1)       null comment '是否在线 0 否 1是',
    is_alarms           int(1)       null comment '是否报警 0 和 “”表示未报警，1 报警',
    is_delete           int(1)       null comment '是否已删除 0 否 1是',
    default_timescale   varchar(8)   null comment '掉线延时  单位（秒）',
    u_id                int(10)      null comment '操作员ID',
    del_sensor_ids      varchar(255) null comment '删除的传感器ID',
    oem_id              int          null comment '经销商ID',
    admin_id            varchar(255) null comment '管理员ID'
)
    comment '设备表';

