create table nts_box_position
(
    id                bigint(32) auto_increment comment '箱位Id'
        primary key,
    boxarea_id        bigint(32)             null comment '箱位区块编号',
    preview_direction tinyint(1)             null comment '预览方向 0-前视图，1-后视图',
    pitch_no_row      int(8)                 null comment '选择的行',
    pitch_no_column   int(8)                 null comment '选择的列',
    child_column      int(8)                 null comment '选择的子列',
    container_id      varchar(64)            null comment '集装箱编号',
    device_id         varchar(64)            null comment '设备编号',
    delete_flag       tinyint(1)  default 1  null comment '删除标志 1-正常状态 2-已删除',
    create_time       datetime               null comment '创建时间',
    update_time       datetime               null comment '更新时间',
    create_by         varchar(64) default '' null comment '创建者',
    update_by         varchar(64) default '' null comment '更新者'
)
    comment '箱位表';

create index index_box_position
    on nts_box_position (container_id, device_id);

