create table nts_device
(
    id                   bigint auto_increment comment '编号'
        primary key,
    dept_id              bigint                 null comment '部门|企业ID',
    user_id              bigint                 null comment '用户ID',
    device_name          varchar(100)           null comment '设备名称',
    device_desc          varchar(200)           null comment '设备描述',
    type_name            varchar(60)            null comment '设备型号',
    device_imei_code     varchar(255)           null comment '设备IMEI码',
    sim_ccid             varchar(64)            null comment 'SIM卡号',
    positioning_mode     varchar(64)            null comment '定位模式',
    tracking_mode        varchar(64)            null comment '追踪模式',
    package_type         varchar(64)            null comment '套餐类型',
    type_id              int(5)                 null comment '类型ID',
    bind_container       varchar(255)           null comment '绑定的集装箱',
    group_name           varchar(60)            null comment '分组名称',
    device_online_status tinyint(2)  default 0  null comment '在线状态',
    group_id             int(5)                 null comment '分组ID',
    activate             tinyint(1)  default 0  null comment '激活状态：0-未激活 1-已激活',
    activate_time        datetime               null comment '激活时间',
    device_state         tinyint(1)  default 1  null comment '设备状态：1-正常 2-停止 3-关闭',
    device_sn            varchar(60)            null comment '设备SN',
    delete_flag          tinyint(1)  default 1  null comment '删除标志 1-正常状态 2-已删除',
    create_time          datetime               null comment '创建时间',
    update_time          datetime               null comment '更新时间',
    create_by            varchar(64) default '' null comment '创建者',
    update_by            varchar(64) default '' null comment '更新者',
    disconnect_time      varchar(64)            null comment '断开连接时间',
    connect_time         varchar(64)            null comment '连接时间'
)
    comment '设备表';

create index index_bind_container
    on nts_device (bind_container);

create index index_dept_id
    on nts_device (dept_id);

create index index_device
    on nts_device (dept_id, device_sn);

create index index_device_online_status
    on nts_device (device_online_status);

