create table nts_device_param
(
    id             int auto_increment
        primary key,
    param_id       int               not null comment '通信ID',
    table_name     varchar(20)       null comment '所在表名',
    table_param    varchar(20)       null comment '所在表字段名',
    param_category int               null comment '参数类型,1:厂家参数，2:用户参数',
    param_name     varchar(50)       null comment '参数中文名',
    param_type     tinyint default 0 null comment '1:16位正整数,2:32位正整数,3:带符号整数，4:ASCII数据，5:Unicode数据',
    `decimal`      tinyint default 0 null comment '小数点位数',
    data_format    varchar(30)       null comment '###.00',
    unit           varchar(10)       null comment '单位',
    is_readonly    tinyint default 0 null comment '1:只读，2:可编辑',
    create_date    datetime          null
);

create index FK_device_param
    on nts_device_param (table_name);

