create table proposal
(
    id           int(10) auto_increment comment 'ID'
        primary key,
    content      varchar(50) default '' not null comment '内容',
    add_time     datetime               not null comment '添加时间',
    pid          int         default 0  not null comment '提交人呢',
    photo_one    varchar(50)            null comment '图片1',
    photo_two    varchar(50)            null comment '图片2',
    photo_three  varchar(50)            null comment '图片3',
    photo_four   varchar(50)            null comment '图片4',
    photo_five   varchar(50)            null comment '图片5',
    update_time  datetime               null,
    remark       longtext               null,
    process_id   int                    null comment '处理人id',
    process_name varchar(50)            null comment '处理人',
    status       tinyint     default 0  not null comment '处理状态0-未处理，1-已处理',
    market_id    int                    null comment '市场id'
)
    comment '建议' engine = MyISAM;

